mapper.select:
select u_name,avatar,c.first_comment_id, c.comment_id,u.u_id,topic_id,parent_id,time,praise_count,content,cul.is_liked,#{userId} as userId
from (knowledge_planet.comment c join knowledge_planet.user u on c.u_id=u.u_id) left join knowledge_planet.comment_user_rel cul on cul.comment_id=c.comment_id
where parent_id is null and topic_id=#{topicId} and (cul.u_id=#{userId} or cul.u_id is null);

select count(*)
from knowledge_planet.comment
where topic_id=#{topicId};

select c1.comment_id, c1.first_comment_id, c1.topic_id, c1.parent_id, c1.time, c1.praise_count, c1.content, c1.u_id,c2.u_id as to_id,ifnull(is_liked,0) as is_liked
from (knowledge_planet.comment c1 join knowledge_planet.comment c2 on c1.parent_id=c2.comment_id) left join knowledge_planet.comment_user_rel cul on cul.comment_id=c1.comment_id
where c1.first_comment_id=#{first_comment_id} and (cul.u_id=#{userId} or cul.u_id is null) and c1.parent_id is not null;

select c1.comment_id,c1.first_comment_id, c1.topic_id, c1.parent_id, c1.time, c1.praise_count, c1.content, c1.u_id,c2.u_id as to_id,ifnull(is_liked,0) as is_liked
from (knowledge_planet.comment c1 join knowledge_planet.comment c2 on c1.parent_id=c2.comment_id) left join knowledge_planet.comment_user_rel cul on cul.comment_id=c1.comment_id
where c1.first_comment_id=#{commentId} and (cul.u_id=#{userId} or cul.u_id is null);

mapper.update:
update knowledge_planet.comment
set praise_count=praise_count+#{count}
where comment_id=#{commentId};
